helperFunctions.abilityModToAttrOption:
function(modifier) {
          switch(modifier) {
            case 'str': return 'Str';
            case 'dex': return 'Ges';
            case 'con': return 'Kon';
            case 'int': return 'Int';
            case 'wis': return 'Wei';
            case 'cha': return 'Cha';
          }
          console.warn('bad ability modifier', modifier);
          return '';
        }

helperFunctions.getNthAttackWeapon:
function(itemNr) {
          return (actor.items.filter(i => i.type === 'weapon' && i.system.equipped && i.hasAttack && i.hasDamage)[itemNr - 1] || '');
        }

helperFunctions.localizedDamageType:
function(item) {
          const dt = item ? item.getDerivedDamageLabel()[0].damageType : null;
          return dt ? game.i18n.localize(game.dnd5e.config.damageTypes[dt]) : '';
        }

helperFunctions.weaponRange:
function(item) {
          if (!item) return '';
          const r = item.system.range;
          return r.value + (r.long ? ("/" + r.long) : "") + (r.units ? (" " + r.units) : "");
        }

helperFunctions.weaponBonus:
function(item) {
          if (!item) return '';
          const rd = item.getAttackToHit().rollData;
          return (+rd.mod)+(+rd.prof)+(+rd.ammo);
        }

helperFunctions.splitIntoTwoParts:
function(text, firstPartMinLength, ratio) {
          if (!text) return '';
          const len = text.length;
          if (len <= firstPartMinLength) {
            return [text, ''];
          }
          const splitIdx = len * ratio;
          const p2 = text.substring(splitIdx);
          const nlIdx = p2.indexOf('\n');

          if (nlIdx >= 0 && nlIdx < 100) {
            return [
              text.substring(0, splitIdx + nlIdx),
              p2.substring(nlIdx + 1)
            ];
          }

          const spcIdx = p2.indexOf(' ');
          if (spcIdx >= 0) {
            return [
              text.substring(0, splitIdx + spcIdx),
              p2.substring(spcIdx + 1)
            ];
          }
          return [text.substring(0, splitIdx), p2];
        }

helperFunctions.getPrimaryClassObj:
function() {
          const allClasses = actor.items.filter(i => i.type === 'class').map(i => i);
          if (allClasses.length > 1) {
            const primary = allClasses.filter(i => i.flags?.srd5e?.isPrimaryClass);
            if (primary.length >= 1) {
              return primary[0];
            }
          }
          return allClasses[0];
        }

helperFunctions.getLocalizedClassAndSubclass:
function(classItem) {
          const sc = classItem.system.subclass;
          return sc ? game.i18n.localize(classItem.name) + "/" + game.i18n.localize(sc) : game.i18n.localize(classItem.name);
        }

helperFunctions.getFeatsAndTraits:
function() {
          let featsAndTraits = '';
          actor.items.filter(i => ['feat', 'trait'].includes(i.type)).map(i => i).forEach(i => {
            featsAndTraits += ("### " + i.name);
            if (i.system?.source) {
              featsAndTraits += (" (" + i.system.source + ")");
            }
            featsAndTraits += " ###\n";
            if (i.system?.description?.value) {
              featsAndTraits += functionSet.htmlToText(i.system.description.value);
              featsAndTraits += "\n";
            }
          });
          return featsAndTraits;
        }
